CREATE TABLE "USUARIOS" (	
    "UUID" NUMBER GENERATED BY DEFAULT AS IDENTITY MINVALUE 1 MAXVALUE 999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER  NOCYCLE  NOKEEP  NOSCALE  NOT NULL ENABLE, 
	"USERNAME" VARCHAR2(100 BYTE) COLLATE "USING_NLS_COMP" NOT NULL ENABLE, 
	"PASSWORD" VARCHAR2(100 BYTE) COLLATE "USING_NLS_COMP" NOT NULL ENABLE
);

CREATE UNIQUE INDEX "USUARIOS_UUID_IDX" ON "USUARIOS" ("UUID");
ALTER TABLE "USUARIOS" ADD CONSTRAINT "USUARIOS_PK" PRIMARY KEY ("UUID") USING INDEX "USUARIOS_UUID_IDX"  ENABLE;

COMMENT ON COLUMN "USUARIOS"."UUID" IS 'Universal ID';
COMMENT ON COLUMN "USUARIOS"."USERNAME" IS 'System username';
COMMENT ON COLUMN "USUARIOS"."PASSWORD" IS 'System password';

