CREATE TABLE "USUARIOS"(	
    "UUID" NUMBER(18,0) GENERATED BY DEFAULT AS IDENTITY MINVALUE 1 MAXVALUE 9999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER  NOCYCLE  NOKEEP  NOSCALE  NOT NULL ENABLE, 
	"USERNAME" VARCHAR2(100 BYTE) UNIQUE NOT NULL ENABLE, 
	"PASSWORD" VARCHAR2(100 BYTE) NOT NULL ENABLE,
    "ROLES_ID" NUMBER(18,0)
);

CREATE UNIQUE INDEX "USUARIOS_UUID_IDX" ON "USUARIOS" ("UUID");
ALTER TABLE "USUARIOS" ADD CONSTRAINT "USUARIOS_PK" PRIMARY KEY ("UUID") USING INDEX "USUARIOS_UUID_IDX"  ENABLE;
ALTER TABLE "USUARIOS" ADD CONSTRAINT "USUARIOS_ROLE_FK" FOREIGN KEY ("ROLES_ID") REFERENCES "ROLES" ("UUID") ENABLE;

COMMENT ON COLUMN "USUARIOS"."UUID" IS 'Universal ID';
COMMENT ON COLUMN "USUARIOS"."USERNAME" IS 'System username';
COMMENT ON COLUMN "USUARIOS"."PASSWORD" IS 'System password';
COMMENT ON COLUMN "USUARIOS"."ROLES_ID" IS 'System Role';

